import { useState } from 'react';
import { createPost } from '../../services/posts'
import { storage } from '../../firebase';
import { ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import {v4} from 'uuid'
import './NewPost.css'


const NewPost = ( {token, userId, toggleStateChange, userImg} ) => {
    const [postMessage, setPostMessage] = useState(''); // State variable to manage new post text
    const [file, setFile] = useState(null) // State variable to manage uploaded file
    const [uploadImage, setUploadImage] = useState(false) // State variable to manage uploading selected image
    const [errorMessage, setErrorMessage] = useState("") // State variable to manage error messages
    const [uploadedImage, setUploadedImage] = useState(null) // STate variable to manage uploaded image

    const uploadImageFirebase = () => {
        // Creates a reference to the storage location for the image.
        // Creates unique name for file using filename + a random UUID e.g. abcd1234
        const imageRef = ref(storage, `post-images/${file.name + v4()}`)
        uploadBytes(imageRef, file)
            .then(() => {
                getDownloadURL(imageRef) // gets imageURL from firebase storage
                    .then((downloadURL) => {
                        createPost(token, userId, downloadURL, postMessage) // attempts to create new post with text / image
                            .then(res => {
                                setPostMessage('') // Clears state
                                setFile(null) // Sets state for uploaded file to null
                                setErrorMessage('') // Clears statue
                                setUploadImage(false) // Clears state
                                setUploadedImage(null) // Sets state for uploaded image to null
                                toggleStateChange() // Triggers re render of feed page

                            })
                            .catch(error => {
                                console.log('error submitting post', error) // Logs error if unable to create new post
                            })   

                    })
                    .catch((error) => {
                        console.error("error getting download URL:", error) // Logs error if unable to retriieve image URL from firebase
                    })
            })
            .catch((error) => {
                console.error("error uploading image", error) // Logs error if error uploading image to firebase
            })
    }

    const handleSubmit = (event) => {
        event.preventDefault()

        if(!file && !postMessage){
            return alert("cannot post empty comment") // Alert warning if no image selected and no text input
        } 

        if (file) {
            uploadImageFirebase() // If file chosen for post, post is created with this function
        } else {
            createPost(token, userId, null, postMessage) // attempts to create new post with just text (null represent no image with post)
                .then(res => {
                    setPostMessage('')
                    setFile(null)
                    setErrorMessage('')
                    setUploadImage(false)
                    setUploadedImage(null)
                    toggleStateChange()

                })
                .catch(error => {
                    console.log('error submitting post', error) // Logs error if unable to create new post
                })   
        }

    }

    // const handleUploadImageClick = () => {
    //     setuploadImage(!uploadImage)
    // }

    const handleFileChange = (e) => {
        setFile(e.target.files[0]) // Sets state to selected image
        setUploadedImage(URL.createObjectURL(e.target.files[0])) // Sets state to selected image
    }

    const closeImage = () => {
        setUploadedImage(null) // Sets state to null 
    }

    {/* returns container displaying current user image, text box, button to upload image, button to post*/}
    return (
        <div className="new-post-container">
            <form onSubmit={handleSubmit}>
                <div className="text-area-container">
                <div className="circle">
                    <img src={userImg}></img>
                </div>
                    <textarea
                        className="new-post-text-area"
                        name="text"
                        value = {postMessage}
                        placeholder="make a new post..."
                        onChange={(message) => setPostMessage(message.target.value)}
                    >
                    </textarea>
                    {uploadedImage &&
                    <div className="uploaded-image-container">
                        <img src={uploadedImage} />
                    </div>
                    }


                </div>

                <div className="new-post-options">
                <div className="image-button-container">
                        <label htmlFor="file-upload" className="custom-file-upload">
                            <i className="fa-solid fa-image"></i>
                            Upload Image
                        </label>
                        <input className="newpost-choose-file"
                            id="file-upload"
                            type="file" 
                            name="file"
                            accept="image/png, image/jpeg" 
                            onChange={handleFileChange}
                        />
                        {uploadedImage && 
                        <div className="delete-image-button-container">
                            <button type='button' className="delete-image-button" onClick={closeImage}>Delete Image</button>
                        </div>
                        }

                </div>

                    <div className="post-button-container">
                        <button type='submit' className="post-button">Post</button>
                    </div>
                </div>

                {errorMessage && errorMessage}
            </form>
        </div>
    )


};

export default NewPost;

import { createNotification } from "../../services/user";
import { likePost } from "../../services/posts";
import "./LikeButton.css"

// Component displays like button as a thumbs up icon.
// Responsible for handling like / unlike of a post by current usewr
const LikeButton = (props) => {

    // Handles logic for click event for liking/unliking a post

    const handleClick = async () => {
        try {
            await likePost(props.token, props.postId, props.userId); // Attempts to update like status for post of current user
            props.setUserLikesPost((previousState) => !previousState); // Toggles the state of whether the user likes the post or not
            props.toggleStateChange && props.toggleStateChange() // Triggers re render of feed page
            props.userPageRender && props.userPageRender(); // Triggers re render of user page
            if (!props.userLikesPost) { // If user has liked the post
                try {
                    const notificationResult = await createNotification({ // Attempt to create notification for post owner
                        username: props.loggedInUsername,
                        entity_userId: props.post_userId,
                        token: props.token,
                        notificationType: "post-like",
                    });
                } catch (error) {
                    console.log(
                        "An error occured while creating a notification" // Log error if unable to create notification
                    );
                }
            } else {
                try { // If user has un-liked the post
                    const notificationResult = await createNotification({ // Attempt to create notification for post owner
                        username: props.loggedInUsername,
                        entity_userId: props.post_userId,
                        token: props.token,
                        notificationType: "post-unlike",
                    });
                } catch (error) {
                    console.log(
                        "An error occured while creating a notification" // Log error if unable to create notification
                    );
                }
            }
        } catch (error) {
            console.error("Error liking/unliking post:", error);
        }
    };

    return (
        <button onClick={handleClick}>
            {props.userLikesPost ? (
                <i
                    className="fa-solid fa-thumbs-up"
                    style={{ color: "red" }}></i>
            ) : (
                <i className="fa-regular fa-thumbs-up"></i>
            )}
        </button>
    );
};

export default LikeButton;

import { useState } from "react";
import { postComment } from "../../services/posts";
import { createNotification } from "../../services/user";
import './AddComment.css'

export default function AddComment({ postId, toggleStateChange, post_userId, userPageRender }) {
	const [commentText, setCommentText] = useState(""); // State for storing the comment text
	const [token, setToken] = useState(window.localStorage.getItem("token")); // State for storing the authentication token
	const [user, setUserId] = useState(
		JSON.parse(window.localStorage.getItem("user")) // State for storing the current user data
	);
	const [errorMessage, setErrorMessage] = useState(""); // State for storing error messages

	const handleChange = (event) => {
		setCommentText(event.target.value); // Update commentText state with input value
	};

	const submitComment = async (event) => {
		event.preventDefault();
		if (commentText.length !== 0) { // Checks if the comment text is not empty
			try {
				const result = await postComment(token, commentText, postId, user._id); // Attempt to post comment
				setCommentText(""); // Clears the comment text input field
				toggleStateChange ? toggleStateChange() : null // Toggles the state change to update the UI for the feed page
				userPageRender ? userPageRender() : null // Toggles the state change to update the UI for the user page

				try {
					const notificationResult = await createNotification({ // Attempt to create a notification for the post owner
                        username: user.username,
						username: user.username,
						entity_userId: post_userId,
						token: token,
						notificationType: "post-comment",
					});
				} catch (error) {
					console.log("An error occured while creating a notification");
				}
			} catch (error) {
				setErrorMessage("An error occured while posting comment");
			}
		} else {
			setErrorMessage("empty comment"); // Sets error message for empty comment
		}
	};

	return (
		<>
		{errorMessage && <p>{errorMessage}</p>} {/* Rendering error message if present */}
		<form onSubmit={submitComment}>
			<div className="text-area-container">
				<textarea
					className="add-comment-text-area"
					type="text"
					placeholder="make a new comment"
					value={commentText}
					onChange={handleChange}
				/>
			</div>
			<div className="submit-comment-button-container">
				<button className="submit-commit-button" type="submit">Post Comment</button>
			</div>
		</form>

		</>
	);
}

// Calculates the time difference between a given dateTime and the current date and time. 
// Formats this difference into a string indicating how long ago the given date and time occurred.
// e.g. a comment was made 2 hours ago

export default function timeFromNow(dateTime) {

    const currentDate = new Date();
    const postDate = new Date(dateTime);

    const timeDifference = currentDate - postDate;
    const secondsDifference = Math.floor(timeDifference / 1000);
    const minutesDifference = Math.floor(secondsDifference / 60);
    const hoursDifference = Math.floor(minutesDifference / 60);
    const daysDifference = Math.floor(hoursDifference / 24);

    if (daysDifference > 30) {
        // If over 30 days, return the actual date
        const options = { year: 'numeric', month: 'long', day: 'numeric' };
        return postDate.toLocaleDateString(undefined, options);
    } else if (hoursDifference >= 24) {
        // If over 24 hours, return days
        return `${daysDifference} day${daysDifference !== 1 ? 's' : ''} ago`;
    } else if (hoursDifference >= 1) {
        // If over 1 hour, return hours
        return `${hoursDifference} hour${hoursDifference !== 1 ? 's' : ''} ago`;
    } else if (minutesDifference >= 1) {
        // If over 1 minute, return minutes
        return `${minutesDifference} minute${minutesDifference !== 1 ? 's' : ''} ago`;
    } else {
        // If under 1 minute, return seconds
        return `${secondsDifference} second${secondsDifference !== 1 ? 's' : ''} ago`;
    }
}

