                {message}
            </div>
        </div>
    </div>
)

}

import './ConfirmDeleteModal.css'
import ConfirmDeleteModal from './ConfirmDeleteModal';
import { useState } from 'react';
import { deleteThePost } from '../../services/posts';

// Component renders a delete button and handles deletion of post after triggering a modal asking user to confirm delete action
const DeleteButton = (props) => {
    const [confirmDeleteModal, setConfirmDeleteModal] = useState(false); // State to track visibility of the confirmation modal

    const handleDeletePostTrue = async () => {
        try {
            const result = await deleteThePost(props.postID, props.token); // Attempt to delete the post
            setConfirmDeleteModal(false); // Close the confirmation modal after deletion
            props.toggleStateChange && props.toggleStateChange() // Trigger state change of feedpage
            props.userPageRender && props.userPageRender() // Trigger state change of user page
		} catch (error) {
			console.error("Error deleting post:", error); // Log error if deletion fails
		}
	};


    const handleDeletePost = () => {
        setConfirmDeleteModal(true) // Show the confirmation modal
    }

    const handleDeletePostFalse = () => {
        setConfirmDeleteModal(false) // Hide the confirmation modal
    }

    return props.showButton ? (
        <>
        <button onClick={handleDeletePost}>
            <i className="fa fa-trash" aria-hidden="true"></i>
        </button>

        { confirmDeleteModal && 
            <ConfirmDeleteModal 
                handleDeletePostTrue={handleDeletePostTrue}
                handleDeletePostFalse={handleDeletePostFalse}
            />
        }
        </>
    ) : null;

};

export default DeleteButton;

import { useState, useEffect, useRef } from "react";
import "./PostText.css"

// Component to render text content of post
export default function PostText(props){
    const [errorMessage, setErrorMessage] = useState("");
    const textareaRef = useRef(null);

    // Effect hook to adjust textarea height based on content
    useEffect(() => {
        const textarea = textareaRef.current;
        if (textarea) {
            textarea.style.height = 'auto';
            textarea.style.height = `${textarea.scrollHeight}px`; 
        }

    },[props.postText])

    return (
        <div>
            {/* Textarea for post text, updates state in parent component when value changes
                Initial value is set to existing post text */}
            <textarea
                ref={textareaRef}
                className = {props.edittingPost ? "post-text-area-editing" : "post-text-area"}
                name="text"
                type="text"
                value={props.postText}
                onChange={(e) => props.setPostText(e.target.value)} 
            >
            </textarea>   
        </div>
    )

}

import "./Post.css";
import "../../pages/User/UserPage.css";
import LikeButton from "../LikeButton/LikeButton";
import { useState, useEffect } from "react";
import AddComment from "../AddComment/AddComment";
import Comment from "../Comment/Comment";
import DeleteButton from "../DeleteButton/DeleteButton";
import PostText from "../PostText/PostText";
import timeFromNow from "../../utils/timeFromNow";
import { Link } from "react-router-dom";
import { editPost } from "../../services/posts";

const Post = (props) => {
    const [user, setUser] = useState(null);
    const [post, setPost] = useState(null)
    const [showMoreComments, setShowMoreComments] = useState(false);
    const [postText, setPostText] = useState("")
    const [edittingPost, setEdittingPost] = useState(false)
    const [userLikesPost, setUserLikesPost] = useState(false)
    const [userIsOwner, setUserIsOwner] = useState(false)

    // Effect hook to set user and post states
    useEffect(() => {
        setUser(JSON.parse(window.localStorage.getItem("user")))
        setPost(props.post)
        setPostText(props.post.message);  
	}, [props.post]);

    // Effect hook to determine if the current user is the owner of the post and if they currently like this post
    useEffect(() => {
        if (user) {
            setUserLikesPost(props.post.likes.includes(user._id));
            setUserIsOwner(props.post.postedBy._id === user._id);
        }
    }, [user, props.post]);


    const handleEditPost = () => {
        setEdittingPost(!edittingPost) // Sets state to opposite of current
    }

    // Function to handle showing all comments
    const showMoreCommentsClick = () => {
        setShowMoreComments(true);  // Sets state true
    };

    // Function to handle hideing
    const hideCommentsClick = () => {
        setShowMoreComments(false); // Sets state false
    };

    // Function to handle editing text of existing post by post owner
    const handleSaveEditText = (event) => {
		event.preventDefault();
		const postData = {
			userId: props.userId,
			postId: props.post._id,
			postMessage: postText,
		};
		editPost(props.token, postData) // Attempts to update post text
			.then(() => {
				props.toggleStateChange();
                props.userPageRender()
			})
			.catch((error) => {
				console.log("Error submitting post:", error);
			});

        handleEditPost() 
	};

    // Sort posts comments by createdAt timestamp and reverse for latest first
    const sortedComments = props.post.comments.sort(
        (a, b) => new Date(a.createdAt) - new Date(b.createdAt)
    );

    return (
        <div key={props.post._id} className="post-container">
            <article>
                {props.postedBy && (
                    <div className="post-header">
                        <div className="post-user-image-container">
                            <Link to={`/users/${props.postedBy.username}`} className="post-user-image">
                                <img src={props.postedBy.image} alt="user image"></img>
                            </Link>
                        </div>
                        <div className="username-time-container">
                            <div className="post-username">
                            <Link to={`/users/${props.postedBy.username}`} className="post-username">
                                {props.postedBy.username}
                            </Link>
                            </div>
                            <div className="date-time">
                                {timeFromNow(props.post.createdAt)}
                            </div>
                        </div>
                    </div>
                )}
                    <div className="post-body">

                    <div className="post-text">
                        <PostText 
                            edittingPost={edittingPost}
                            postText={postText}
                            setPostText={setPostText}
                        />
                    </div>

                        {props.post.media  && (
                            <div className="post-image-container">
                                <img src={props.post.media}></img>
                            </div>
                        )}


                    </div>

                    <div className="post-footer"> 
                        <div className="like-container">
                            <div className="like-button-container">
                            <LikeButton
                                postId={props.post._id}
                                setUserLikesPost={setUserLikesPost}
                                token={props.token}
                                toggleStateChange={props.toggleStateChange}
                                userPageRender={props.userPageRender}
                                liked={props.liked}
                                userLikesPost={userLikesPost}
                                post_userId={props.postedBy._id}
                                loggedInUsername={props.loggedInUsername}
                                userId={props.userId}
                            />
                            </div>
                            <div className="like-number">
                                {props.post.likes.length}
                            </div>
                        </div>
                            <div className="delete-post-button-container">
                                {userIsOwner ? ( 
                                    edittingPost ? (
                                        <button onClick={handleSaveEditText}>save</button>
                                    ) : (
                                        <button onClick={handleEditPost}>edit</button>
                                    )
                                ) : null}

                                <DeleteButton
                                    postID={props.post._id}
                                    token={props.token}
                                    toggleStateChange={props.toggleStateChange}
                                    userPageRender={props.userPageRender}
                                    onDelete={props.onDelete}
                                    showButton={userIsOwner}
                                />

                            </div>
                    </div>

                    <div className="add-comment-container">
                        <AddComment
                            postId={props.post._id}
                            toggleStateChange={props.toggleStateChange}
                            post_userId={props.postedBy._id}
                            triggerStateChange={props.triggerStateChange}
                            userPageRender={props.userPageRender}
                        />
                    </div>

                    <div className="comments-container">

                        {sortedComments.length > 0 && (
                            <Comment
                                _id={sortedComments[0]._id}
                                message={sortedComments[0].message}
                                likes={sortedComments[0].likes}
                                // postedBy={comment.user.username}
                                postedAt={sortedComments[0].createdAt}
                                user={sortedComments[0].user}
                            />
                        )}

                        <div className="more-comments-button-container">
                            {sortedComments.length > 1 && !showMoreComments && (
                                <button onClick={showMoreCommentsClick}>see more</button>
                            )}
                        </div>

                        {showMoreComments &&
                            sortedComments
                                .slice(1)
                                .map((comment) => (
                                    <Comment
                                        key={comment._id}
                                        _id={comment._id}
                                        message={comment.message}
                                        likes={comment.likes}
                                        postedAt={comment.createdAt}
                                        user={comment.user}
                                    />
                                ))}

                        <div className="hide-comments-button-container">
                        {showMoreComments && (
                            <button onClick={hideCommentsClick}>hide</button>
                        )}
                        </div>
                </div>
            </article>
        </div>
    );
};

export default Post;
