import { Link } from "react-router-dom"
import './SearchResultsDropDown.css'
import { useEffect, useRef } from "react"

// Component
export default function SearchResultsDropDown( { foundUsers, setShowSearchResults } ) {
    const dropdownRef = useRef(null)

    useEffect(() => {
        const handleClickOutside = (event) => {
            // Checks if click event occured outside component
            if (dropdownRef.current && !dropdownRef.current.contains(event.target)) {
                setShowSearchResults(false) // Close the search results dropdown
            }
        }
        // Adds event listener for mousedown clicks and passed event to function
        document.addEventListener('mousedown', handleClickOutside)

        // Remove event listener when the component unmounts
        return () => {
            document.removeEventListener('mousedown', handleClickOutside)
        }
            }, [dropdownRef])


    return (
        <div ref={dropdownRef} className="search-results">

        {/* if no users found displays message */}
        {foundUsers.length === 0 ?
        <div className="no-users-found"> 
            No users found
        </div>
            :
            <>
            {/* if user(s) found from search displays results showing user profile image and username as clickable link to their profile */}
            {foundUsers.map((user) => {
                return ( 
                    <Link to={`/users/${user.username}`} className="found-user" key={user._id}>

                    <div className="user-image-container">
                        <img  className="user-image" src={user.image}></img>
                    </div>
                    <div className="user-name-link">
                            {user.username}
                    </div>

                    </Link>
                )
            }
            )}
            </>

            }
        </div>

    )


}

import { useState } from "react"
import { searchUsers } from "../../services/user"
import SearchResultsDropDown from "./SearchResultsDropDown"
import "./SearchNavItem.css"

export default function SearchNavItem( { handleSearch }) {
    const [searchUserInput, setSearchUserInput] = useState("") // State to manage user input in search box
    const [token, setToken] = useState(window.localStorage.getItem("token"))
    const [foundUsers, setFoundUsers] = useState([]) // State to manage list of users found during search

    const handleInputChange = (event) => {
        const inputValue = event.target.value;
        setSearchUserInput(inputValue) // Updates the search input value
        searchUsers(inputValue) // Attempts to find users based on the current input value
            .then((data) => {
                setFoundUsers(data.result) // Update the found users list
                handleSearch(data.result) // Passes search results to function in navbar component
            })
            .catch((err) => {
                console.error(err);
                setFoundUsers([]); // Reset the found users list
                handleSearch([]); // Pass an empty array to function in navbar component
            });
    }

    const handleSubmit = (event) => {
        event.preventDefault()
        searchUsers(searchUserInput)
            .then((data) => {
                setFoundUsers(data.result)
                handleSearch(data.result)
            })
            .catch((err) => {
                console.error(err);
                setFoundUsers([])
                handleSearch(foundUsers)
            });
    }

    return (
        <>
        <form onSubmit={handleSubmit}>
            <input className="search-user-input" id="search-box" type="input"
                placeholder="Search user"
                value= {searchUserInput}
                onChange={handleInputChange}
                autoComplete="off"
            />
        </form>

        </>
    )



}

